import React, { useState } from "react";
import { SketchPicker } from "react-color";
import "./ColorPicker.css";
import useEventListener from "./../use-event-listener";

export const ColorPicker = ({ defineColor, text, defineRgbBoardColor }) => {
  const [visiblePallete, setVisiblePallete] = useState(false);
  const [backgroundColor, setBackgroundColor] = useState("#fff");
  const [rgbColor, setRgbColor] = useState({
    choose: 0,
  });

  const keyboardChoser = (e) => {
    if (e.keyCode === 13) {
      setRgbColor({
        ...rgbColor,
        choose: rgbColor.choose + 1,
      });
    }

    if (rgbColor.choose === 1) {
      if (e.keyCode === 38) {
        setRgbColor({
          ...rgbColor,
          r: rgbColor.r + 1,
          g: rgbColor.g,
          b: rgbColor.b,
        });
      } else if (e.keyCode === 40) {
        setRgbColor({
          ...rgbColor,
          r: rgbColor.r - 1,
          g: rgbColor.g,
          b: rgbColor.b,
        });
      }
    }

    if (rgbColor.choose === 2) {
      if (e.keyCode === 38) {
        setRgbColor({
          ...rgbColor,
          r: rgbColor.r,
          g: rgbColor.g + 1,
          b: rgbColor.b,
        });
      } else if (e.keyCode === 40) {
        setRgbColor({
          ...rgbColor,
          r: rgbColor.r,
          g: rgbColor.g - 1,
          b: rgbColor.b,
        });
      }
    }

    if (rgbColor.choose === 3) {
      if (e.keyCode === 38) {
        setRgbColor({
          ...rgbColor,
          r: rgbColor.r,
          g: rgbColor.g,
          b: rgbColor.b + 1,
        });
      } else if (e.keyCode === 40) {
        setRgbColor({
          ...rgbColor,
          r: rgbColor.r,
          g: rgbColor.g,
          b: rgbColor.b - 1,
        });
      }
    }
  };

  const handleChangeComplete = (color) => {
    setRgbColor({
      ...rgbColor,
      r: color.rgb.r,
      g: color.rgb.g,
      b: color.rgb.b,
    });
    setBackgroundColor(color.hex);
    defineColor(color.hex);
  };

  useEventListener("keydown", keyboardChoser);
  return (
    <div className="color-picker">
      <button onClick={() => setVisiblePallete(!visiblePallete)}>{text}</button>
      {visiblePallete && (
        <SketchPicker
          color={backgroundColor}
          onChangeComplete={handleChangeComplete}
        />
      )}
    </div>
  );
};
