import React, { useState } from "react";
import { SafeZone } from "./SafeZone";
import useEventListener from "./../use-event-listener";

export const Board = ({ textColor, boardColor, safeZoneVisible }) => {
  const [textSize, setTextSize] = useState(50);

  const changeTextSize = (e) => {
    if (e.keyCode === 187) {
      setTextSize(textSize + 1);
    } else if (e.keyCode === 189) {
      setTextSize(textSize - 1);
    }
  };

  useEventListener("keydown", changeTextSize);

  const boardStyle = {
    backgroundColor: `${boardColor}`,
    height: `${1080 / 2}px`,
    width: `${1920 / 2}px`,
  };
  console.log(boardColor);
  return (
    <div className="board" style={boardStyle}>
      {safeZoneVisible && <SafeZone />}
      <div className="text" style={{ color: `${textColor}` }}>
        <h2 style={{ fontSize: `${textSize - 10}px` }}>
          Star Wars - The Last Jedi
        </h2>
        <h3 style={{ fontSize: `${textSize - 20}px` }} className="mediumtext">
          A small river named Duden flows by their place and supplies it.
        </h3>
        <h5 style={{ fontSize: `${textSize - 35}px` }} className="mediumtext">
          Far far away, behind the word mountains, far from the countries
          Vokalia and a Consonantia, there live the blind texts. Separated they
          live in Bookmarksgrove right at the coast of the Semantics, a large
          language ocean.
        </h5>
      </div>
    </div>
  );
};
