import React, { useState } from "react";
import "./App.css";
import "./Board.css";
import { ColorPicker } from "./components/ColorPicker";

function App() {
  const [boardColor, setBoardColor] = useState("");
  const [textColor, setTextColor] = useState("");
  const [keyboardColor, setKeyboardColor] = useState({
    state: false,
    rgbChoose: 0,
    board: { r: 0, g: 0, b: 0 },
    text: { r: 0, g: 0, b: 0 },
  });

  const defineBoardColor = (color) => {
    setBoardColor(color);
  };

  const defineTextColor = (color) => {
    setTextColor(color);
  };

  const keyboardChoser = (e) => {
    if (e.keyCode === 13) {
    setKeyboardColor({...keyboardColor, rgbChoose: keyboardColor.rgbChoose+1})
    }
  };

  return (
    <div className="App" onKeyDown={this.keyboardChoser}>
      <div className="board" style={{ backgroundColor: `${boardColor}` }}>
        <div className="text" style={{ color: `${textColor}` }}>
          Hey
        </div>
      </div>
      <ColorPicker
        text={"Choose your board Color"}
        defineColor={defineBoardColor}
      />
      <ColorPicker
        text={"Choose your text Color"}
        defineColor={defineTextColor}
      />
    </div>
  );
}

export default App;
