import React, { useState, useRef } from "react";
import { SketchPicker } from "react-color";
import "./ColorPicker.css";
import useEventListener from "./../use-event-listener";
import { rgbToHex } from "./RGBtoHex";
import { hexToRGB } from "./RGBtoHex";
import useOnClickOutside from "../useOnClickOutside";

export const ColorPicker = ({
  defineColor,
  text,
  defineButton,
  button,
  initialColor,
  defineRgbType,
  changeColor,
}) => {
  const [visiblePallete, setVisiblePallete] = useState(false);
  const [backgroundColor, setBackgroundColor] = useState(initialColor);
  const [rgbColor, setRgbColor] = useState({
    choose: 0,
    r: hexToRGB(initialColor)[0],
    g: hexToRGB(initialColor)[1],
    b: hexToRGB(initialColor)[2],
  });
  const [presetColors, setPresetColors] = useState([
    "#D0021B",
    "#F5A623",
    "#F8E71C",
    "#8B572A",
    "#7ED321",
    "#417505",
    "#BD10E0",
    "#9013FE",
    "#4A90E2",
    "#50E3C2",
  ]);

  const keyCodesColors = [[49,50,51,52,53,54,55,56,57,48]]
  const [isTV, setIsTV] = useState(false);
  const ref = useRef();

  useOnClickOutside(ref, () => setVisiblePallete(false));

  const openWithKeyboard = (e) => {
    e.preventDefault();

    if (
      (e.keyCode === 37 && button === "text") ||
      (e.keyCode === 39 && button === "board")
    ) {
      setIsTV(true);

      setRgbColor({
        ...rgbColor,
        choose: 1,
        r: rgbColor.r,
        g: rgbColor.g,
        b: rgbColor.b,
      });

      click();
      handleChangeCompleteTV(backgroundColor);
    }
  };

  const keyboardChoser = (e) => {
    e.preventDefault();
    if (visiblePallete && e.keyCode === 13) {
      if (rgbColor.choose === 3) {
        setRgbColor({
          ...rgbColor,
          choose: 1,
        });
      } else {
        setRgbColor({
          ...rgbColor,
          choose: rgbColor.choose + 1,
        });
      }
    }

    if (rgbColor.choose === 1) {
      if (e.keyCode === 38) {
        setRgbColor({
          ...rgbColor,
          r: rgbColor.r + 1,
          g: rgbColor.g,
          b: rgbColor.b,
        });
      } else if (e.keyCode === 40) {
        setRgbColor({
          ...rgbColor,
          r: rgbColor.r - 1,
          g: rgbColor.g,
          b: rgbColor.b,
        });
      }
    }

    if (rgbColor.choose === 2) {
      if (e.keyCode === 38) {
        setRgbColor({
          ...rgbColor,
          r: rgbColor.r,
          g: rgbColor.g + 1,
          b: rgbColor.b,
        });
      } else if (e.keyCode === 40) {
        setRgbColor({
          ...rgbColor,
          r: rgbColor.r,
          g: rgbColor.g - 1,
          b: rgbColor.b,
        });
      }
    }

    if (rgbColor.choose === 3) {
      if (e.keyCode === 38) {
        setRgbColor({
          ...rgbColor,
          r: rgbColor.r,
          g: rgbColor.g,
          b: rgbColor.b + 1,
        });
      } else if (e.keyCode === 40) {
        setRgbColor({
          ...rgbColor,
          r: rgbColor.r,
          g: rgbColor.g,
          b: rgbColor.b - 1,
        });
      }
    }

    setBackgroundColor(rgbToHex(rgbColor.r, rgbColor.g, rgbColor.b));
    defineColor(rgbToHex(rgbColor.r, rgbColor.g, rgbColor.b));
    defineRgbType(rgbColor.choose);
  };

  const handleChangeComplete = (color) => {
    setBackgroundColor(color.hex);
    defineColor(color.hex);
  };

  const handleChangeCompleteTV = (color) => {
    setBackgroundColor(color);
    defineColor(color);
  };

  const click = () => {
    setVisiblePallete(!visiblePallete);
    defineButton(button);
    changeColor();
  };

  const colorWithNumber = (e) => {
    e.preventDefault();
    if (visiblePallete) {
      if (e.keyCode === 49) {
        setRgbColor({
          ...rgbColor,
          r: hexToRGB(presetColors[0])[0],
          g: hexToRGB(presetColors[0])[1],
          b: hexToRGB(presetColors[0])[2],
        });
        handleChangeCompleteTV(presetColors[0]);
      } else if (e.keyCode === 50) {
        setRgbColor({
          ...rgbColor,
          r: hexToRGB(presetColors[1])[0],
          g: hexToRGB(presetColors[1])[1],
          b: hexToRGB(presetColors[1])[2],
        });
        handleChangeCompleteTV(presetColors[1]);
      }
    }
  };

  useEventListener("keydown", keyboardChoser);
  useEventListener("keydown", openWithKeyboard);
  useEventListener("keydown", colorWithNumber);

  return (
    <div className="color-picker">
      <button onClick={click}>{text}</button>
      {visiblePallete && (
        <div ref={ref}>
          <SketchPicker
            color={backgroundColor}
            onChangeComplete={
              isTV ? handleChangeCompleteTV : handleChangeComplete
            }
            presetColors={presetColors}
          />
        </div>
      )}
    </div>
  );
};
