import React, { useState, useEffect } from "react";
import useEventListener from "./use-event-listener";
import "./App.css";
import "./Board.css";
import { ColorPicker } from "./components/ColorPicker";

function App() {
  const [boardColor, setBoardColor] = useState("");
  const [textColor, setTextColor] = useState("");
  const [keyboardColor, setKeyboardColor] = useState({
    state: false,
    rgbChoose: 0,
    board: { r: 0, g: 0, b: 0 },
    text: { r: 0, g: 0, b: 0 },
  });

  const defineBoardColor = (color) => {
    setBoardColor(color);
  };

  const defineTextColor = (color) => {
    setTextColor(color);
  };

  const keyboardChoser = (e) => {
    if (e.keyCode === 13) {
      setKeyboardColor({
        ...keyboardColor,
        rgbChoose: keyboardColor.rgbChoose + 1,
      });
    }

    if (keyboardColor.rgbChoose === 1) {
      if (e.keyCode === 38) {
        setKeyboardColor({
          ...keyboardColor,
          board: {
            r: keyboardColor.board.r + 1,
            g: keyboardColor.board.g,
            b: keyboardColor.board.b,
          },
        });
      } else if (e.keyCode === 40) {
        setKeyboardColor({
          ...keyboardColor,
          board: {
            r: keyboardColor.board.r - 1,
            g: keyboardColor.board.g,
            b: keyboardColor.board.b,
          },
        });
      } else if (e.keyCode === 37) {
        setKeyboardColor({
          ...keyboardColor,
          text: {
            r: keyboardColor.text.r - 1,
            g: keyboardColor.text.g,
            b: keyboardColor.text.b,
          },
        });
      } else if (e.keyCode === 39) {
        setKeyboardColor({
          ...keyboardColor,
          text: {
            r: keyboardColor.text.r + 1,
            g: keyboardColor.text.g,
            b: keyboardColor.text.b,
          },
        });
      }
    }

    if (keyboardColor.rgbChoose === 2) {
      if (e.keyCode === 38) {
        setKeyboardColor({
          ...keyboardColor,
          board: {
            r: keyboardColor.board.r,
            g: keyboardColor.board.g + 1,
            b: keyboardColor.board.b,
          },
        });
      } else if (e.keyCode === 40) {
        setKeyboardColor({
          ...keyboardColor,
          board: {
            r: keyboardColor.board.r,
            g: keyboardColor.board.g - 1,
            b: keyboardColor.board.b,
          },
        });
      } else if (e.keyCode === 37) {
        setKeyboardColor({
          ...keyboardColor,
          text: {
            r: keyboardColor.text.r,
            g: keyboardColor.text.g - 1,
            b: keyboardColor.text.b,
          },
        });
      } else if (e.keyCode === 39) {
        setKeyboardColor({
          ...keyboardColor,
          text: {
            r: keyboardColor.text.r,
            g: keyboardColor.text.g + 1,
            b: keyboardColor.text.b,
          },
        });
      }
    }

    if (keyboardColor.rgbChoose === 3) {
      if (e.keyCode === 38) {
        setKeyboardColor({
          ...keyboardColor,
          board: {
            r: keyboardColor.board.r,
            g: keyboardColor.board.g,
            b: keyboardColor.board.b + 1,
          },
        });
      } else if (e.keyCode === 40) {
        setKeyboardColor({
          ...keyboardColor,
          board: {
            r: keyboardColor.board.r,
            g: keyboardColor.board.g,
            b: keyboardColor.board.b - 1,
          },
        });
      } else if (e.keyCode === 37) {
        setKeyboardColor({
          ...keyboardColor,
          text: {
            r: keyboardColor.text.r,
            g: keyboardColor.text.g,
            b: keyboardColor.text.b - 1,
          },
        });
      } else if (e.keyCode === 39) {
        setKeyboardColor({
          ...keyboardColor,
          text: {
            r: keyboardColor.text.r,
            g: keyboardColor.text.g,
            b: keyboardColor.text.b + 1,
          },
        });
      }
    }
  };

  useEventListener("keydown", keyboardChoser);

  return (
    <div className="App">
      <div className="board" style={{ backgroundColor: `${boardColor}` }}>
        <div className="text" style={{ color: `${textColor}` }}>
          Hey
        </div>
      </div>
      <ColorPicker
        text={"Choose your board Color"}
        defineColor={defineBoardColor}
      />
      <ColorPicker
        text={"Choose your text Color"}
        defineColor={defineTextColor}
      />
    </div>
  );
}

export default App;
