import React, { useState } from "react";
import { SketchPicker } from "react-color";
import "./ColorPicker.css";
import useEventListener from "./../use-event-listener";

export const ColorPicker = ({ defineColor, text }) => {
  const [visiblePallete, setVisiblePallete] = useState(false);
  const [backgroundColor, setBackgroundColor] = useState("#fff");
  const [rgbColor, setRgbColor] = useState({
    choose: 0,
    rgb: { r: 0, g: 0, b: 0 },
  });
  const [keyboardColor, setKeyboardColor] = useState({
    state: false,
    rgbChoose: 0,
    board: { r: 0, g: 0, b: 0 },
    text: { r: 0, g: 0, b: 0 },
  });

  const keyboardChoser = (e,color) => {
    if (e.keyCode === 13) {
      setRgbColor({
        ...rgbColor,
        choose: rgbColor.choose + 1,
      });
    }

    if (keyboardColor.rgbChoose === 1) {
      if (e.keyCode === 38) {
        setRgbColor({
          ...rgbColor,
          r: color.rgb.r + 1,
          g: color.rgb.g,
          b: color.rgb.b,
        });
      } else if (e.keyCode === 40) {
        setKeyboardColor({
          ...keyboardColor,
          board: {
            r: keyboardColor.board.r - 1,
            g: keyboardColor.board.g,
            b: keyboardColor.board.b,
          },
        });
      } else if (e.keyCode === 37) {
        setKeyboardColor({
          ...keyboardColor,
          text: {
            r: keyboardColor.text.r - 1,
            g: keyboardColor.text.g,
            b: keyboardColor.text.b,
          },
        });
      } else if (e.keyCode === 39) {
        setKeyboardColor({
          ...keyboardColor,
          text: {
            r: keyboardColor.text.r + 1,
            g: keyboardColor.text.g,
            b: keyboardColor.text.b,
          },
        });
      }
    }

    if (keyboardColor.rgbChoose === 2) {
      if (e.keyCode === 38) {
        setKeyboardColor({
          ...keyboardColor,
          board: {
            r: keyboardColor.board.r,
            g: keyboardColor.board.g + 1,
            b: keyboardColor.board.b,
          },
        });
      } else if (e.keyCode === 40) {
        setKeyboardColor({
          ...keyboardColor,
          board: {
            r: keyboardColor.board.r,
            g: keyboardColor.board.g - 1,
            b: keyboardColor.board.b,
          },
        });
      } else if (e.keyCode === 37) {
        setKeyboardColor({
          ...keyboardColor,
          text: {
            r: keyboardColor.text.r,
            g: keyboardColor.text.g - 1,
            b: keyboardColor.text.b,
          },
        });
      } else if (e.keyCode === 39) {
        setKeyboardColor({
          ...keyboardColor,
          text: {
            r: keyboardColor.text.r,
            g: keyboardColor.text.g + 1,
            b: keyboardColor.text.b,
          },
        });
      }
    }

    if (keyboardColor.rgbChoose === 3) {
      if (e.keyCode === 38) {
        setKeyboardColor({
          ...keyboardColor,
          board: {
            r: keyboardColor.board.r,
            g: keyboardColor.board.g,
            b: keyboardColor.board.b + 1,
          },
        });
      } else if (e.keyCode === 40) {
        setKeyboardColor({
          ...keyboardColor,
          board: {
            r: keyboardColor.board.r,
            g: keyboardColor.board.g,
            b: keyboardColor.board.b - 1,
          },
        });
      } else if (e.keyCode === 37) {
        setKeyboardColor({
          ...keyboardColor,
          text: {
            r: keyboardColor.text.r,
            g: keyboardColor.text.g,
            b: keyboardColor.text.b - 1,
          },
        });
      } else if (e.keyCode === 39) {
        setKeyboardColor({
          ...keyboardColor,
          text: {
            r: keyboardColor.text.r,
            g: keyboardColor.text.g,
            b: keyboardColor.text.b + 1,
          },
        });
      }
    }
  };

  const handleChangeComplete = (color, e) => {
    if (e.keyCode === 13) {
      setRgbColor({
        ...rgbColor,
        choose: rgbColor.choose + 1,
      });
    }
    setRgbColor({
      ...rgbColor,
      r: color.rgb.r,
      g: color.rgb.g,
      b: color.rgb.b,
    });
    setBackgroundColor(color.hex);
    defineColor(color.hex);

    if(e.keyCode === 38) {
      setRgbColor({
        ...rgbColor,
        r: color.rgb.r+1,
      g: color.rgb.g,
      b: color.rgb.b,
      })
    }
  };

  useEventListener("keydown", keyboardChoser);
  return (
    <div className="color-picker">
      <button onClick={() => setVisiblePallete(!visiblePallete)}>{text}</button>
      {visiblePallete && (
        <SketchPicker
          color={backgroundColor}
          onChangeComplete={handleChangeComplete}
        />
      )}
    </div>
  );
};
