import React, { useState } from "react";
import { SketchPicker } from "react-color";

export const ColorPicker = () => {
  const [color, setColor] = useState({ background: "#fff" });

  handleChangeComplete = (color) => {
    this.setState({ background: color.hex });
  };

  return (
    <div>
      <SketchPicker />
    </div>
  );
};
