import React, { useState } from "react";
import { SketchPicker } from "react-color";
import "./ColorPicker.css";

export const ColorPicker = ({ defineBoardColor }) => {
  const [visiblePallete, setVisiblePallete] = useState(false);
  const [backgroundColor, setBackgroundColor] = useState('#fff');

  const handleChangeComplete = (colorr) => {
    setBackgroundColor( colorr );

    defineBoardColor(color.background);
  };

  console.log(color.background);

  return (
    <div className="color-picker">
      <button onClick={() => setVisiblePallete(!visiblePallete)}>
        Choose your color
      </button>
      {visiblePallete && (
        <SketchPicker
          color={color.background}
          onChangeComplete={handleChangeComplete}
        />
      )}
      <button onClick={() => console.log(color.background)}></button>
    </div>
  );
};
